import React, { useState, useEffect } from 'react';

const Products = () => {
    const [data, setProducts] = useState([]);
    const [filter, setFilter] = useState(data);
    const [loading, setLoading] = useState(false);
    let componentMounted = true;

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            const response = await 
            Products.get("http://192.168.18.1:8000/products");
            if (componentMounted) {
                setProducts();
                setFilter(await response.json());
                setLoading(false);
                console.log(filter)
            }

            return () => {
                componentMounted = false;
            }
        }
        fetchData();
    }, []);
    const Loading = () => {
        return (
            <>
                Loading....
            </>
        );
    };
    const ShowProducts = () => {
        return (
            <>
                <div className='buttons d-flex justify-content-center mb-5 pb-5'>
                    <div className="btn btn-outline-dark me-2">
                        All
                    </div>
                    <div className="btn btn-outline-dark me-2">
                        Citrus
                    </div>
                    <div className="btn btn-outline-dark me-2">
                        Tropical Fruits
                    </div>
                    <div className="btn btn-outline-dark me-2">
                        Berries
                    </div>
                    <div className="btn btn-outline-dark me-2">
                        Melons
                    </div>
                    <div className="btn btn-outline-dark me-2">
                        Core Fruits
                    </div>
                </div>
                {filter.map((product) => {
                    return (
                        <>
                            <div className="col-md-3 mb-4">
                                <div class="card h-100 text-center p-4" key={product.id}>
                                    <img src={product.image} class="card-img-top" alt={product.name} height="250px" />
                                    <div class="card-body">
                                        <h5 class="card-title mb-0">{product.name.substring(0,12)}</h5>
                                        <p class="card-text lead fw-bold">{product.new_price}</p>
                                        <a href="/" class="btn btn-outline-dark">Order Now</a>
                                    </div>
                                </div>
                            </div>
                        </>
                    )
                })}
            </>
        );
    }
    return (
        <div>
            <div className="container my-5 py-5">
                <div className="row">
                    <div className="col-12 mb-5">
                        <h1 className='display-6 fw-bolder text-center'>New Fresh Fruits</h1>
                        <hr />
                    </div>
                </div>
                <div className="row justify-content-center">
                    {loading ? <Loading /> : < ShowProducts />}
                </div>
            </div>
        </div>
    )
}

export default Products
